def instruction_values(field):
    values = [["height",0],["horizont",0]]
    i = 0
    while(i < len(field)):
        if(field[i][0] == "forward"):
            values[1][1] = values[1][1] + field[i][1]
        elif(field[i][0] == "up"):
            values[0][1] = values[0][1] - field[i][1]
        elif(field[i][0] == "down"):
            values[0][1] = values[0][1] + field[i][1]
        i = i + 1

    return values

def instruction_valuse_two(field):
    values = [["height", 0], ["horizont", 0], ["aim", 0]]
    i = 0
    while (i < len(field)):
        if (field[i][0] == "forward"):
            values[1][1] = values[1][1] + field[i][1]
            values[0][1] = values[0][1] + (values[2][1] * field[i][1])
        elif (field[i][0] == "up"):
            values[2][1] = values[2][1] - field[i][1]
        elif (field[i][0] == "down"):
            values[2][1] = values[2][1] + field[i][1]
        i = i + 1

    return values

